mapper.insert:

insert into adjudication_amendment_record (ID, CASE_ID, CONTENT_ID, 
  STATUS, APPLY_TYPE, CREATER_ID, 
  CREATE_TIME, APPLICATION, FACT_REASON, 
  CONTENT, ARBITRATOR_CONTENT, REMARK
  )
values (#{id,jdbcType=VARCHAR}, #{caseId,jdbcType=VARCHAR}, #{contentId,jdbcType=VARCHAR}, 
  #{status,jdbcType=CHAR}, #{applyType,jdbcType=CHAR}, #{createrId,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{application,jdbcType=LONGVARCHAR}, #{factReason,jdbcType=LONGVARCHAR}, 
  #{content,jdbcType=LONGVARCHAR}, #{arbitratorContent,jdbcType=LONGVARCHAR}, #{remark,jdbcType=LONGVARCHAR}
  )

mapper.select:
SELECT
,
FROM adjudication_amendment_record WHERE status <> ${com.zyxy.common.enums.CaseApproveStatusEnum@WAIT_AUDIT.getValue()} AND case_id = #{caseId}
