mapper.insert:

insert into quiz_record (ID, CASE_ID, QUIZ_OBJECT, 
  ANSWER_TIME, RESPONDENT_ANSWER_TIME, STATUS, 
  RESPONDENT_STATUS, CREATER_ID, CREATE_TIME, 
  QUIZ_CONTENT, QUIZ_ANSWER, RESPONDENT_ANSWER
  )
values (#{id,jdbcType=VARCHAR}, #{caseId,jdbcType=VARCHAR}, #{quizObject,jdbcType=VARCHAR}, 
  #{answerTime,jdbcType=TIMESTAMP}, #{respondentAnswerTime,jdbcType=TIMESTAMP}, #{status,jdbcType=CHAR}, 
  #{respondentStatus,jdbcType=CHAR}, #{createrId,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{quizContent,jdbcType=LONGVARCHAR}, #{quizAnswer,jdbcType=LONGVARCHAR}, #{respondentAnswer,jdbcType=LONGVARCHAR}
  )

update.foreach:

UPDATE quiz_record   

  quiz_answer = #{item.quizAnswer},
  answer_time = now(),
  status = #{status}

where id = ${item.id}


UPDATE quiz_record   

  respondent_answer = #{item.quizAnswer},
  respondent_answer_time = now(),
  status = #{status}

where id = ${item.id}
